<?php\n\n$file = 'path/to/your/file.csv';\n\n// Open the file in read mode\n$handle = fopen($file, 'r');\nif (!$handle) {\n    die('Cannot open file: ' . $file);\n}\n\n// Read the entire file into a string and normalize line endings\n$content = fread($handle, filesize($file));\n$content = str_replace(["\r\n", "\r"], "\n", $content);\n\n// Close the file handle\nfclose($handle);\n\n// Use an in-memory stream to allow fgetcsv to process the normalized content\n$stream = fopen('php://memory', 'r+');\nfwrite($stream, $content);\nrewind($stream);\n\n// Process each line with fgetcsv\nwhile (($data = fgetcsv($stream, 0, ',')) !== false) {\n    // Do something with your data\n    print_r($data);\n}\n\n// Close the in-memory stream\nfclose($stream);\n\n?>