require 'path/to/PHPMailer/src/Exception.php';\nrequire 'path/to/PHPMailer/src/PHPMailer.php';\nrequire 'path/to/PHPMailer/src/SMTP.php';\n\nuse PHPMailer\PHPMailer\PHPMailer;\nuse PHPMailer\PHPMailer\Exception;\n\n$mailer = new PHPMailer(true);\n\ntry {\n    //Server settings\n    $mailer->isSMTP();\n    $mailer->Host       = 'smtp.example.com';                     \n    $mailer->SMTPAuth   = true;                                   \n    $mailer->Username   = 'user@example.com';                    \n    $mailer->Password   = 'secret';                               \n    $mailer->SMTPSecure = PHPMailer::ENCRYPTION_STARTTLS;\n    $mailer->Port       = 587;\n\n    //Recipients\n    $mailer->setFrom('webmaster@example.com', 'Webmaster');\n\n    //Loop through your emails and send individual messages\n    for($k=0; $k <= $x->length - 1; $k++) {\n        for($l=0; $l <= $j-1; $l++) {\n            if($y->item($k)->nodeValue == $JobNoArr[$l] && $AcceptanceDateArr[$l] == '0000-00-00') {               \n                $email = $v->item($k)->nodeValue;\n                \n                $mailer->addAddress($email);\n\n                //Content\n                $mailer->isHTML(true);\n                $mailer->Subject = 'My subject';\n                $mailer->Body    = 'Hello world!';\n                $mailer->AltBody = 'Hello world!';\n\n                if (!$mailer->send()) {\n                    echo "Message could not be sent to $email. Mailer Error: {$mailer->ErrorInfo}";\n                } else {\n                    echo "Message has been sent to $email successfully.";\n                }\n\n                // Clear addresses and attachments for next loop\n                $mailer->clearAddresses();\n            }\n        }\n    }\n} catch (Exception $e) {\n    echo "Message could not be sent. Mailer Error: {$mailer->ErrorInfo}";\n}