// On the sending side\n$data = "Your data here";\n$secret_key = "YourSecretKey"; // This should be kept secret and identical on both sides\n\n$hmac_signature = hash_hmac('sha1', $data, $secret_key);\n\n$request_url = "http://receiving-site.com/receive.php";\n$post_data = http_build_query(array(\n    'data' => base64_encode($data),\n    'signature' => $hmac_signature\n));\n\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $request_url);\ncurl_setopt($ch, CURLOPT_POST, true);\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $post_data);\ncurl_exec($ch);\ncurl_close($ch);\n\n// On the receiving side\nif (!empty($_POST['data']) && !empty($_POST['signature'])) {\n    $received_data = base64_decode($_POST['data']);\n    $received_signature = $_POST['signature'];\n\n    // Create a signature using your secret key and compare it with the received signature\n    $calculated_signature = hash_hmac('sha1', $received_data, 'YourSecretKey');\n\n    if (hash_equals($calculated_signature, $received_signature)) {\n        echo "Data is authentic and has not been tampered with.";\n    } else {\n        echo "Invalid data or integrity compromised.";\n    }\n}