<?php\n\nfunction isCronJob() {\n    // Method 1: Check if php has no arguments (typically true in cron jobs)\n    if ($_SERVER['argc'] == 0) {\n        return true;\n    }\n\n    // Method 2: Check the SAPI name, 'cli' for command line and 'embed' for cron on some systems\n    $sapiType = php_sapi_name();\n    if (strpos($sapiType, 'cli') !== false) {\n        // Additional check to differentiate cron and manual CLI execution\n        return isset($_SERVER['CRON_JOB']) && $_SERVER['CRON_JOB'] === 'true';\n    }\n\n    return false;\n}\n\n// Example usage:\nif (isCronJob()) {\n    // Run code specific to cron job execution, e.g., notifications\n} else {\n    // Run code for manual execution only\n}\n\n?>