<?php\n\nrequire_once 'path/to/WordNet.php';\n\n$wordnet = new \PhpWordnet\WordNet();\n\nfunction lemmatize($word, $pos) {\n    global $wordnet;\n    // POS tags: noun (n), verb (v), adjective (a), adverb (r)\n    $lemma = $wordnet->getLemmaForPos($word, WordNet::POS_NOUN);\n    if ($lemma) {\n        return $lemma[0];\n    }\n    return $word;\n}\n\n$words = ["community", "communities"];\nlemmatizedWords = array_map(function($word) { return lemmatize($word, 'n'); }, $words);\n\nprint_r($lemmatizedWords);\n?>