// In your Item.php Model file\n\npublic $actsAs = array('Containable');\n\npublic function beforeFind($options) {\n    if (!empty($this->virtualFields['is_owned'])) return $options;\n\n    $user_id = CakeSession::read('Auth.User.id'); // Ensure you handle guest users accordingly\n    $this->virtualFields['is_owned'] = "EXISTS(SELECT 1 FROM items_users WHERE item_id = Item.id AND user_id = {$user_id})";\n\n    // You might want to clean up the SQL injection risk in production\n\n    return parent::beforeFind($options);\n}\n\n// In your ItemsController.php\npublic function index() {\n    $this->paginate['Item'] = array(\n        'fields' => array('id', 'name', 'is_owned'),\n        // Add other pagination settings here\n    );\n    $items = $this->paginate('Item');\n    $this->set(compact('items'));\n}