// In your controller\n$this->template = new View('template');\n$this->template->innerView = new View('article');\n\n// Assume you have some data you want to share\n$data_to_share = array(\n    'title' => 'My Title',\n    'content' => 'This is the article content.'\n);\n\n// Assign data to the parent view\n$this->template->set($data_to_share);\n\n// Assign the same data to the child view if it needs access to these variables\n$this->template->innerView->set($data_to_share);\n\n// Render the template in your controller action\necho $this->template;