// Define roles and their permissions for pages\n$permissions = [\n    'admin' => ['home', 'dashboard', 'students', 'teachers'],\n    'city_user' => ['home', 'dashboard', 'students'],\n    'national_user' => ['home', 'dashboard', 'students', 'all_students']\n];\n\n// Function to check if a user has access to a page\nfunction canAccessPage($userRole, $page) {\n    global $permissions;\n    return in_array($page, $permissions[$userRole]);\n}