function areParenthesesBalanced($string) {\n    $stack = [];\n    $length = strlen($string);\n\n    for ($i = 0; $i < $length; $i++) {\n        if ($string[$i] === '(') {\n            array_push($stack, '(');\n        } elseif ($string[$i] === ')') {\n            if (empty($stack)) {\n                return false;\n            }\n            array_pop($stack);\n        }\n    }\n\n    return empty($stack);\n}\n\n// Test cases\n$testStrings = [\n    "This is (ok)",\n    "This (is) (ok)",\n    "This is )bad(",\n    "This is also (bad",\n    "This is (bad (too))"\n];\n\nforeach ($testStrings as $string) {\n    echo "$string: " . (areParenthesesBalanced($string) ? "Valid" : "Invalid") . "\n";\n}