public function userProposals() {\n    $username = User::getUsername();\n    $state = User::userState();\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_user', 'your_password');\n    $sqlTemplates = [\n        'petition' => ['SELECT * FROM petition WHERE author = :username', 'Petition'],\n        'proposition' => ['SELECT * FROM proposition WHERE author = :username', 'Proposition'],\n        'amendment' => ['SELECT * FROM amendment WHERE author = :username', 'Amendment']\n    ];\n\n    foreach ($sqlTemplates as $type => list($sql, $typeName)) {\n        $stmt = $pdo->prepare($sql);\n        $stmt->execute([':username' => $username]);\n        while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n            echo "\n                <tr>\n                    <td>{$row['id']}</td>\n                    <td><a href='viewproposal.php?type=$type&id={$row['id']}'>{$row['title']}</a></td>\n                    <td>$typeName</td>\n                    <td>$state</td>\n                </tr>";\n        }\n    }\n}