// Entity definition using Annotations\nuse Doctrine\ORM\Mapping as ORM;\n\n/**\n * @ORM\Entity(repositoryClass="MyProject\Repository\UserRepository")\n * @ORM\Table(name="users")\n */\nclass User\n{\n    /**\n     * @ORM\Id\n     * @ORM\GeneratedValue(strategy="AUTO")\n     * @ORM\Column(type="integer")\n     */\n    private $id;\n\n    /**\n     * @ORM\Column(type="string", length=255)\n     */\n    private $username;\n\n    // Getters and setters...\n}\n\n// Fluent mapping example in Configuration\nuse Doctrine\ORM\Mapping\Driver\AnnotationDriver;\nuse Doctrine\ORM\EntityManager;\nuse Doctrine\ORM\Configuration;\n\n$config = new Configuration();\n$driver = new AnnotationDriver(\n    new \Doctrine\Common\Annotations\AnnotationReader(),\n    __DIR__ . '/path/to/entities'\n);\n$config->setMetadataDriverImpl($driver);\n\n// Setting up the EntityManager\n$entityManager = EntityManager::create(['dbname' => 'example'], $config);\n\n// Using the repository and entity manager\n$userRepository = $entityManager->getRepository(User::class);\n$newUser = new User();\n$newUser->setUsername('johndoe');\n$entityManager->persist($newUser);\n$entityManager->flush();