<?php\n// Set maximum post size (if not set by server)\nini_set('post_max_size', '50M');\n\n// Set maximum upload file limit (if not set by server)\nini_set('upload_max_filesize', '50M');\n\n// Check for upload errors and display relevant message\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    if (!empty($_FILES['file'])) {\n        $uploadErrors = [\n            UPLOAD_ERR_OK           => "There is no error, the file uploaded with success.",\n            UPLOAD_ERR_INI_SIZE     => "The uploaded file exceeds the upload_max_filesize directive in php.ini.",\n            UPLOAD_ERR_FORM_SIZE    => "The uploaded file exceeds the MAX_FILE_SIZE directive specified in the HTML form.",\n            UPLOAD_ERR_PARTIAL      => "The uploaded file was only partially uploaded.",\n            UPLOAD_ERR_NO_FILE      => "No file was uploaded.",\n            UPLOAD_ERR_NO_TMP_DIR   => "Missing a temporary folder. Introduced in PHP 5.0.3.",\n            UPLOAD_ERR_CANT_WRITE   => "Failed to write file to disk. Introduced in PHP 5.1.0.",\n            UPLOAD_ERR_EXTENSION    => "A PHP extension stopped the file upload. PHP does not provide a way to ascertain which extension caused the file upload stop; examining the list of loaded extensions with phpinfo() may help."\n        ];\n\n        $errCode = $_FILES['file']['error'];\n        if ($errCode == UPLOAD_ERR_OK) {\n            // File uploaded successfully, continue processing\n            echo "File uploaded successfully.";\n        } else {\n            // Display error message related to file upload failure\n            echo "Error uploading file: " . $uploadErrors[$errCode];\n        }\n    } else {\n        echo "No file was sent for upload.";\n    }\n}\n?>