<?php\nrequire_once __DIR__ . '/vendor/autoload.php'; // Ensure to install mPDF via Composer\n\nuse Mpdf\Mpdf;\n\n// Create mpdf object\n$mpdf = new Mpdf();\n\n// Simple HTML template for an Invoice\n$html = '\n<html>\n<head>\n<style>\n    body { font-family: Arial, sans-serif; }\n    table { border-collapse: collapse; width: 100%; }\n    th, td { border: 1px solid black; padding: 8px; text-align: left; }\n</style>\n</head>\n<body>\n\n<h2>Invoice</h2>\n<p>Date: ' . date('d-m-Y') . '</p>\n<table>\n    <tr>\n        <th>Item</th>\n        <th>Description</th>\n        <th>Quantity</th>\n        <th>Price</th>\n        <th>Total</th>\n    </tr>\n    <tr>\n        <td>1</td>\n        <td>Service A</td>\n        <td>2</td>\n        <td>$50.00</td>\n        <td>$100.00</td>\n    </tr>\n    <!-- Add more rows as needed -->\n</table>\n\n<p>Total: $100.00</p>\n\n<!-- Barcode Generation (assuming 'php-barcode' is included or similar library) -->\n<img src="barcode.php?c=UPC-A&text=123456789012" alt="Barcode" />\n\n</body>\n</html>\n';\n\n// Write HTML to PDF\n$mpdf->WriteHTML($html);\n\n// Output a PDF file to the browser.\n$mpdf->Output('invoice.pdf', 'I');