// Function to get next or previous tweet based on current key and direction\nfunction getNextOrPreviousTweet($key, $direction) {\n    // First, find the current tweet_id\n    $sql = "SELECT tweet_id FROM tweets WHERE tweet_key = '$key' LIMIT 1";\n    $result = mysqli_query($connection, $sql) or die("DB Error : ". mysqli_error($connection));\n    $currentRow = mysqli_fetch_assoc($result);\n\n    if ($currentRow) {\n        $currentId = $currentRow['tweet_id'];\n\n        // Determine the SQL query based on direction\n        if ($direction == 'next') {\n            $sql = "SELECT * FROM tweets WHERE tweet_id > '$currentId' ORDER BY tweet_id ASC LIMIT 1";\n        } else {\n            $sql = "SELECT * FROM tweets WHERE tweet_id < '$currentId' ORDER BY tweet_id DESC LIMIT 1";\n        }\n\n        // Execute the query\n        $result = mysqli_query($connection, $sql) or die("DB Error : ". mysqli_error($connection));\n        return mysqli_fetch_assoc($result);\n    } else {\n        return null; // No current row found\n    }\n}