$body = file_get_contents('index.htm');\n\n// Define the HTML piece you want to remove.\n$htmlToRemove = '<td><table><tr><td></td></tr></table></td>';\n\n// Conditionally remove the HTML piece.\nif (shouldRemoveTable()) { // Replace with your actual condition.\n    $body = str_replace($htmlToRemove, '', $body);\n}\n\nfunction shouldRemoveTable() {\n    // Your logic to determine whether the table should be removed.\n    return true; // or false based on your criteria\n}\n\necho $body;