<?php\n// Database connection setup\n$servername = "localhost";\n$username = "your_username";\n$password = "your_password";\n$dbname = "newspapers";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Query to search through the OCRed text\nfunction performSearch($conn, $searchTerm) {\n    // Prepare SQL statement with full-text search\n    $stmt = $conn->prepare("SELECT id, newspaper, issue, date, page_number \n                            FROM newspaper_pages \n                            WHERE MATCH(ocr_content) AGAINST(? IN NATURAL LANGUAGE MODE)");\n    \n    if ($stmt === false) {\n        die("Prepare failed: " . $conn->error);\n    }\n    \n    // Bind search term\n    $stmt->bind_param("s", $searchTerm);\n    $stmt->execute();\n    \n    // Get result\n    $result = $stmt->get_result();\n    while ($row = $result->fetch_assoc()) {\n        echo "ID: " . $row["id"] . ", Newspaper: " . $row["newspaper"] . \n             ", Issue: " . $row["issue"] . ", Date: " . $row["date"] . \n             ", Page Number: " . $row["page_number"] . "<br>";\n    }\n    \n    $stmt->close();\n}\n\n// Example usage\n$searchTerm = $_GET['q'];\nperformSearch($conn, $searchTerm);\n\n$conn->close();\n?>