$body = file_get_contents('index.htm');\n\n$dom = new DOMDocument();\n@$dom->loadHTML($body); // Suppress errors due to malformed HTML\n\n// Remove the specific table inside a <td>\nforeach ($dom->getElementsByTagName('td') as $td) {\n    foreach ($td->childNodes as $childNode) {\n        if ($childNode->nodeType === XML_ELEMENT_NODE && $childNode->nodeName === 'table') {\n            // Add more checks if necessary before removing\n            $td->removeChild($childNode);\n        }\n    }\n}\n\n$body = $dom->saveHTML();\n\necho $body;