<?php\nsession_start(); // Start session to track state\n\nif ($_SERVER['REQUEST_METHOD'] == 'POST') {\n    if (isset($_POST['cancel'])) {\n        header('Location: overview.php'); // Go back to the overview page\n        exit;\n    }\n\n    if (isset($_FILES['photo']) && $_FILES['photo']['error'] === UPLOAD_ERR_OK) {\n        $tmp_name = $_FILES['photo']['tmp_name'];\n        $name = basename($_FILES['photo']['name']);\n\n        $_SESSION['uploaded_file'] = [\n            'tmp_name' => $tmp_name,\n            'name' => $name\n        ];\n\n        // Redirect for final decision\n        header('Location: confirm_upload.php');\n        exit;\n    }\n}\n?>\n\n<!-- Your HTML form -->\n<form method="POST" action="" enctype="multipart/form-data">\n    <input type="file" name="photo" required>\n    <button type="submit" name="submit">Submit</button>\n    <button type="reset">Reset Form</button> <!-- This is a reset not cancel but it clears form -->\n    <button type="submit" name="cancel">Cancel</button>\n</form>