public function dateTimeToUnixTime($dateTimeString) {\n    if (strlen($dateTimeString) < 18) {\n        return false;\n    }\n    \n    $date = DateTime::createFromFormat('Y-m-d H:i:s', $dateTimeString, new DateTimeZone('UTC'));\n    if ($date === false) return false;\n    \n    // Convert to GMT Unix timestamp\n    return $date->getTimestamp();\n}\n\n// Usage:\n$nowUnix = $this->dateTimeToUnixTime(date('Y-m-d H:i:s', time()));\n$userLastActiveUnix = $this->dateTimeToUnixTime($date);\n\n$lastSeen = abs($nowUnix - $userLastActiveUnix);