// Always store dates in GMT\ndate_default_timezone_set('UTC');\n$db->query("SET SESSION time_zone = '+00:00'");\n\npublic function updateStatus()\n{\n    global $db;\n    \n    // Use NOW() UTC to ensure it's stored in GMT\n    $data = array(\n        'date_last_active'  => new Zend_Db_Expr('NOW(UTC)')\n    );\n    \n    $db->update('users', $data, ['user_id = ?' => $this->userId]);\n}