<?php\n$xmlString = $var; // Assuming $var contains your XML string from the Curl request\n\n// Load the XML string into a SimpleXMLElement object\n$xml = simplexml_load_string($xmlString);\n\nif ($xml === false) {\n    echo "Failed loading XML: ";\n    foreach(libxml_get_errors() as $error) {\n        echo "\n", $error->message;\n    }\n} else {\n    // Iterate over each skill element in the professions\n    foreach ($xml->skill as $skill) {\n        // Access attributes like key, maxage, group, and worked\n        $key = (string)$skill['key'];\n        $maxAge = (int)$skill['maxage'];\n        $group = (string)$skill['group'];\n        $worked = (int)$skill['worked'];\n\n        echo "Key: $key, Max Age: $maxAge, Group: $group, Worked: $worked\n";\n    }\n}\n?>