// Refactored class\nclass InputSanitizer {\n    public function sanitizeInput(array $inputData) {\n        // Assume some sanitization logic here\n        return array_map('trim', filter_var_array($inputData, FILTER_SANITIZE_STRING));\n    }\n}\n\n// PHPUnit Test\nuse PHPUnit\Framework\TestCase;\n\nclass InputSanitizerTest extends TestCase {\n    protected $sanitizer;\n\n    protected function setUp(): void {\n        parent::setUp();\n        $this->sanitizer = new InputSanitizer();\n    }\n\n    public function testSanitizeInput() {\n        $inputData = [\n            'name' => "  John<script>alert('XSS')</script>Doe  ",\n            'email' => "test@example.com   "\n        ];\n        \n        $expectedOutput = [\n            'name' => "JohnDoe",\n            'email' => "test@example.com"\n        ];\n\n        $this->assertEquals($expectedOutput, $this->sanitizer->sanitizeInput($inputData));\n    }\n}