function cleanXmlString($xmlString) {\n    // Remove NULL character\n    $cleanXmlString = str_replace("\x00", '', $xmlString);\n    \n    // Optionally, remove other control characters (except whitespace)\n    $cleanXmlString = preg_replace('/[\x01-\x1F\x7F]/', '', $cleanXmlString);\n\n    return $cleanXmlString;\n}\n\n$xmlString = 'Your XML string with potential NULL characters here...';\n$cleanedXmlString = cleanXmlString($xmlString);\n\ntry {\n    $simpleXmlElement = new SimpleXMLElement($cleanedXmlString);\n    // Proceed with further processing\n} catch (Exception $e) {\n    echo "Error creating SimpleXMLElement: " . $e->getMessage();\n}