<?php\nfunction encrypt($plaintext) {\n    $password = "YourSecretPassword"; // Must use a stronger key in production\n    $method = 'aes-256-cbc'; // Choose cipher method\n    $ivlen = openssl_cipher_iv_length($method);\n    $iv = openssl_random_pseudo_bytes($ivlen);\n    $ciphertext_raw = openssl_encrypt($plaintext, $method, $password, OPENSSL_RAW_DATA, $iv);\n    $hmac = hash_hmac('sha256', $ciphertext_raw, $password, true);\n\n    return base64_encode( $iv.$hmac.$ciphertext_raw );\n}\n\n$formData = '<form method="post" action="#">...Your form data here...</form>';\n$encryptedForm = encrypt($formData);\n?>