function connectToDatabase($servers) {\n    $connection = null;\n    foreach ($servers as $server) {\n        list($host, $user, $pass, $dbname) = $server;\n        try {\n            // Using PDO for database connection\n            $dsn = "mysql:host=$host;dbname=$dbname;charset=utf8mb4";\n            $connection = new PDO($dsn, $user, $pass);\n            $connection->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n            break;\n        } catch (PDOException $e) {\n            // Log the error and attempt to connect using the next server in line\n            error_log("Failed to connect to database at $host: " . $e->getMessage());\n            continue;\n        }\n    }\n\n    if (!$connection) {\n        throw new Exception("Could not connect to any of the provided databases.");\n    }\n\n    return $connection;\n}\n\n// Example usage:\n$servers = [\n    ['localhost', 'user1', 'pass1', 'db1'],\n    ['secondary-db.example.com', 'user2', 'pass2', 'db2']\n];\n\ntry {\n    $pdo = connectToDatabase($servers);\n    echo "Connected to the database successfully.";\n} catch (Exception $e) {\n    echo "Failed to connect: " . $e->getMessage();\n}