<?php\n\n// Define the base directory for configuration files\n$baseConfigDir = __DIR__ . '/config';\n\n// Default to 'development' if no ENV is set\n$env = getenv('APP_ENV') ?: 'development';\n\n// Determine the configuration file path\n$configFilePath = $baseConfigDir . '/' . $env . '.php';\n\nif (!file_exists($configFilePath)) {\n    die("Configuration file not found for environment: " . $env);\n}\n\n// Load the appropriate configuration file\n$config = require_once $configFilePath;\n\n// Use $config array for database, paths, etc.\n$dbHost = $config['database']['host'];\n$dbUser = $config['database']['user'];\n$dbPass = $config['database']['password'];\n\n// Example output\necho "Database Host: " . $dbHost . "\n";\necho "Database User: " . $dbUser . "\n";