<?php\n\n// Get initial resource usage\n$startUsage = getrusage();\n\n// Your code here\n// For example:\n$sleepTime = 2; // Simulate a workload with sleep\nsleep($sleepTime); // Sleep does not consume CPU time for this purpose\n\n$sqlQuery = "SELECT * FROM some_table"; // Example SQL query\n$result = mysqli_query($connection, $sqlQuery);\n\n// Get final resource usage\n$endUsage = getrusage();\n\n// Calculate the difference in user and system time used\n$userTime = ($endUsage["ru_utime.tv_sec"] + $endUsage["ru_utime.tv_usec"]/1000000) - \n            ($startUsage["ru_utime.tv_sec"] + $startUsage["ru_utime.tv_usec"]/1000000);\n\n$sysTime = ($endUsage["ru_stime.tv_sec"] + $endUsage["ru_stime.tv_usec"]/1000000) - \n           ($startUsage["ru_stime.tv_sec"] + $startUsage["ru_stime.tv_usec"]/1000000);\n\necho "User CPU Time: $userTime seconds\n";\necho "System CPU Time: $sysTime seconds\n";\n\n?>