<?php\n// Connection parameters for Database 1\n$servername = "localhost";\n$username1 = "user1";\n$password1 = "password1";\n$dbname1 = "database1";\n\n// Connection parameters for Database 2\n$username2 = "user2";\n$password2 = "password2";\n$dbname2 = "database2";\n\n// Create connection to Database 1\n$conn1 = new mysqli($servername, $username1, $password1, $dbname1);\n\n// Check connection\nif ($conn1->connect_error) {\n    die("Connection failed: " . $conn1->connect_error);\n}\n\n// Create connection to Database 2\n$conn2 = new mysqli($servername, $username2, $password2, $dbname2);\n\n// Check connection\nif ($conn2->connect_error) {\n    die("Connection failed: " . $conn2->connect_error);\n}\n\n// Example query from Database 1\n$sql1 = "SELECT * FROM table1";\n$result1 = $conn1->query($sql1);\n\nif ($result1->num_rows > 0) {\n    // Output data of each row\n    while($row = $result1->fetch_assoc()) {\n        // Insert into Database 2\n        $value1 = $row['column1'];\n        $value2 = $row['column2'];\n\n        // Prepare and bind\n        $sql2 = "INSERT INTO table2 (column1, column2) VALUES (?, ?)";\n        $stmt = $conn2->prepare($sql2);\n        if ($stmt === false) {\n            die('Error preparing statement: ' . $conn2->error);\n        }\n        $stmt->bind_param("ss", $value1, $value2);\n\n        // Execute the statement\n        if (!$stmt->execute()) {\n            echo "Error inserting data into Database 2: " . $stmt->error;\n        }\n\n        $stmt->close();\n    }\n} else {\n    echo "0 results in Database 1";\n}\n\n// Close connections\n$conn1->close();\n$conn2->close();\n?>