<?php\n$start = time();\n\n// Start from a number whose square is 102030405060708090, and increment by even numbers only.\nfor ($i=1010374999; $i < 1421374999; $i += 2) { \n    $square = $i * $i;\n    \n    // Check the pattern without converting to string\n    if ($square % 10 == 0 &&  // Last digit is 0\n        intval(($square / 100) % 10) == 9 && // Second last digit is 9\n        intval(($square / 10000) % 10) == 8 && // And so on...\n        intval(($square / 1000000) % 10) == 7 &&\n        intval(($square / 100000000) % 10) == 6 &&\n        intval(($square / 10000000000) % 10) == 5 &&\n        intval(($square / 1000000000000) % 10) == 4 &&\n        intval(($square / 100000000000000) % 10) == 3 &&\n        intval(($square / 10000000000000000) % 10) == 2 &&\n        intval(($square / 1000000000000000000) % 10) == 1) {\n        \n        echo $i;\n        break;\n    }\n}\n\n$end = time();\n$elapsed = ($end-$start);\necho "<br />The time to calculate was $elapsed seconds";\n?>