<?php\n\n$idToCount = [\n    1 => 5,\n    2 => 3,\n    3 => 8\n];\n\n$idToName = [\n    1 => 'Alice',\n    2 => 'Bob',\n    3 => 'Charlie'\n];\n\n$nameToCount = [];\n\n// Iterate over the idToName array and use it to map names to counts from idToCount\nforeach ($idToName as $id => $name) {\n    if (isset($idToCount[$id])) { // Check if there is a corresponding count\n        $nameToCount[$name] = $idToCount[$id];\n    }\n}\n\nprint_r($nameToCount);\n\n?>