<?php\n$array = [\n    'apple',\n    'orange',\n    'pear',\n    'banana',\n    'apple',\n    'pear',\n    'kiwi',\n    'kiwi',\n    'kiwi'\n];\n\n// Count occurrences of each value in the array\n$counts = array_count_values($array);\n\n// Iterate over the counts and print them\nforeach ($counts as $key => $count) {\n    if ($count > 1) {\n        echo "$key ($count)\n";\n    } else {\n        echo "$key\n";\n    }\n}\n?>