<?php\n\nfunction check_mod_rewrite() {\n    // Create a temporary file for testing\n    $testFile = 'rewrite_test.php';\n    $content = "<?php echo 'Rewrite works!'; ?>";\n\n    if (file_put_contents($testFile, $content) === FALSE) {\n        die("Unable to create test file.");\n    }\n\n    // Try accessing the non-existent file which should be rewritten\n    ob_start();\n    include 'non_existent_url_should_rewrite_to_test.php';\n    $output = ob_get_clean();\n\n    // Cleanup the test file\n    unlink($testFile);\n\n    return strpos($output, 'Rewrite works!') !== false;\n}\n\nif (check_mod_rewrite()) {\n    echo "mod_rewrite is enabled.";\n} else {\n    echo "mod_rewrite is not enabled or not working properly.";\n}\n?>