<?php\nrequire_once './vendor/autoload.php';\n\nuse IXR_Client;\n\n// Create an XML-RPC client instance\n$client = new IXR_Client('http://your-drupal-site/xmlrpc.php');\n\n// Set up user credentials and blog ID (often 1)\n$username = 'your-username';\n$password = 'your-password';\n$blogId = 1;\n$title = "Test Node";\n$content = "This is a test node created via XML-RPC.";\n\n$params = [$blogId, $username, $password, ['post_type' => 'page', 'title' => $title, 'description' => $content]];\n\n// Attempt to create a new post\nif (!$client->query('metaWeblog.newPost', $params)) {\n    die('An error occurred - ' . $client->getErrorCode() . ': ' . $client->getErrorMessage());\n}\n\n$postId = $client->getResponse();\necho "New node created with ID: " . $postId;\n?>