<?php\n    $xmldoc = new DOMDocument();\n    // Suppress warnings for simplicity, but handle errors in production\n    if (!$xmldoc->load('sample.xml')) {\n        die("Error loading XML file");\n    }\n\n    $xpathvar = new DomXPath($xmldoc);\n\n    // Select lemonade with supplier attribute set to "mother"\n    $queryResult = $xpathvar->query('//lemonade[@supplier="mother"]/price');\n    \n    if ($queryResult->length === 0) {\n        echo "No matching element found.";\n    } else {\n        foreach($queryResult as $result) {\n            echo $result->textContent . "\n";\n        }\n    }\n\n    // Optionally, test with id attribute\n    $queryResultById = $xpathvar->query('//lemonade[@id="1"]/price');\n\n    if ($queryResultById->length === 0) {\n        echo "No matching element found for id=1.";\n    } else {\n        foreach($queryResultById as $result) {\n            echo $result->textContent . "\n";\n        }\n    }\n?>