$sql = mysql_query("SELECT t.thread_id, t.thread_title, MAX(p.post_date) AS last_post_date \n                     FROM threads t \n                     JOIN posts p ON t.thread_id = p.thread_id \n                     GROUP BY t.thread_id \n                     ORDER BY last_post_date DESC \n                     LIMIT 65");\n\nwhile($row = mysql_fetch_array($sql)){\n    echo "<li>Thread ID: " . htmlspecialchars($row['thread_id']) . " - " . htmlspecialchars($row['thread_title']) . " (Last Post: " . $row['last_post_date'] . ")</li>";\n}