<?php\n// Assuming $conn is your PDO or mysqli connection\n\nfunction getLastId($tableName) {\n    global $conn; // In case of procedural style with mysqli\n\n    try {\n        if ($conn instanceof PDO) { // For PDO connection\n            $stmt = $conn->prepare("SELECT id FROM " . $tableName . " ORDER BY id DESC LIMIT 1");\n            $stmt->execute();\n            return $stmt->fetchColumn();\n        } elseif ($conn) { // For mysqli connection\n            $result = $conn->query("SELECT id FROM " . $tableName . " ORDER BY id DESC LIMIT 1");\n            if ($row = $result->fetch_assoc()) {\n                return $row['id'];\n            }\n        }\n    } catch (PDOException $e) {\n        echo "Error: " . $e->getMessage();\n    }\n\n    return null; // Return null if no data is found or an error occurs\n}\n\n// Usage\n$tableName = 'your_table_name';\n$lastId = getLastId($tableName);\nif ($lastId !== null) {\n    echo "The last ID is: " . $lastId;\n} else {\n    echo "No records found or an error occurred.";\n}\n?>