try {\n    $pdo = new PDO("mysql:host=your_host;dbname=your_db", "username", "password");\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->prepare("\n        INSERT INTO :storehistory (col1, col2, col3) \n        SELECT col1, col2, col3 FROM :storetbl WHERE :time - datecreated >= 432000\n    ");\n    \n    // Bind parameters if necessary (adjust as per your actual data)\n    $stmt->execute([":storehistory" => $storehistory, ":storetbl" => $storetbl, ":time" => $time]);\n\n} catch (PDOException $e) {\n    die("Query failed: " . $e->getMessage());\n}