<?php\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\n// Start output buffering\nob_start();\n\nfunction log_and_exit($message, $file = __FILE__, $line = __LINE__) {\n    error_log("Exit called with message: '$message' at file: $file line: $line");\n    ob_end_flush();\n    exit($message);\n}\n\n// Example usage of the function\nlog_and_exit('Exiting here for demonstration');\n\necho "This will never display";\n\n?>