<?php\n\n$descriptorspec = array(\n    0 => STDIN,\n    1 => array("pipe", "w"),\n    2 => STDOUT,\n);\n\n$cwd = '/path/to/php/scripts';\n$env = NULL;\n\n$process = proc_open('php another-script.php', $descriptorspec, $pipes, $cwd, $env);\n\nif (is_resource($process)) {\n    fclose($pipes[0]);\n    echo stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n    $return_value = proc_close($process);\n}