// Assuming $value is the object decoded from your JSON\n\n// Sort the rows based on the 'sort' property\nusort($value->row, function($a, $b) {\n    return $a->sort - $b->sort;\n});\n\n// Output the sorted rows\nforeach ($value->row as $therow) {\n    echo "<div class='row " . htmlspecialchars($therow->type, ENT_QUOTES, 'UTF-8') . "'>";\n\n    foreach ($therow->widgets as $thewidgets) {\n        echo "<div class='widget'>";\n        echo htmlspecialchars($thewidgets->values[0], ENT_QUOTES, 'UTF-8');\n        echo "</div>";\n    }\n\n    echo "</div>";\n}