// Load the source image as true color\n$image = imagecreatefromgif($source_file);\n$trueColorImage = imagecreatetruecolor(imagesx($image), imagesy($image));\nimagealphablending($trueColorImage, false);\nimagesavealpha($trueColorImage, true);\n\n// Copy the GIF to the true color image\nimagecopy($trueColorImage, $image, 0, 0, 0, 0, imagesx($image), imagesy($image));\n\n// Merge watermark onto the true color image\n$watermark = imagecreatefrompng('path/to/watermark.png'); // Ensure watermark is in a format like PNG or GIF with transparency\nimagecopymerge($trueColorImage, $watermark, $x, $y, 0, 0, imagesx($watermark), imagesy($watermark), 65);\n\n// Save the modified image as GIF (or consider saving as PNG)\nimagegif($trueColorImage, $source_file);\n\n// Free up memory\nimagedestroy($image);\nimagedestroy($trueColorImage);\nimagedestroy($watermark);