<?php\n// Define the root path of the application\ndefine('APPLICATION_PATH', realpath(dirname(__FILE__)));\n\n// Adjust the include_path\nset_include_path(\n    implode(PATH_SEPARATOR, array(\n        APPLICATION_PATH . '/lib',\n        APPLICATION_PATH . '/app/classes',\n        get_include_path(),\n    ))\n);\n\n// Check if the file exists before including it\n$zendLoaderPath = 'Zend/Loader.php';\nif (file_exists($zendLoaderPath)) {\n    require_once $zendLoaderPath;\n    Zend_Loader::registerAutoload();\n} else {\n    die("Failed to load {$zendLoaderPath}");\n}\n\n// Run the application.\nApp_Main::run('production');