<?php\n$fileId = $_GET['id']; // Ensure this is sanitized\n$filePath = "/path/to/storage/" . substr($fileId, 0, 3) . '/' . substr($fileId, 3, 3) . '/' . $fileId; // Example path\n\nif (file_exists($filePath)) {\n    header("Location: /direct_path/".basename($filePath)); // Ensure this path is secured and restricted\n} else {\n    http_response_code(404);\n    echo "File not found.";\n}\n?>