<?php\n$mc = new Memcached();\n$mc->addServer('localhost', 11211);\n\nfunction getCachedUserProfile($userId) {\n    global $mc;\n    $cacheKey = "user_profile_{$userId}";\n    if (($data = $mc->get($cacheKey)) !== false) {\n        return $data; // Data was cached\n    } else {\n        // Fetch data from database\n        $userData = getUserDataFromDatabase($userId);\n        $mc->set($cacheKey, $userData, 300); // Cache for 5 minutes\n        return $userData;\n    }\n}\n\nfunction updateUserProfile($userId) {\n    // Update user profile in the database\n    updateUserDataInDatabase($userId);\n\n    // Invalidate cache\n    global $mc;\n    $cacheKey = "user_profile_{$userId}";\n    $mc->delete($cacheKey);\n}\n?>