$dom = new DOMDocument();\n$dom->loadXML($response->getBody());\n\n$xpath = new DOMXPath($dom);\n$xpath->registerNamespace("a", "http://www.example.com");\n\n// Query for the nodes directly\n$books = $xpath->query('//a:Books', $dom);\n\n$bookData = [];\nfor ($i = 0; $i < $books->length; $i++) {\n    $bookNode = $books->item($i);\n    // Access text content and attributes\n    $title = trim($bookNode->textContent); // Trim to remove whitespace\n    $deweyDecimal = $bookNode->getAttribute('DeweyDecimal');\n\n    // Store them in an associative array or as needed\n    $bookData[$i] = [\n        'Title' => $title,\n        'DeweyDecimal' => $deweyDecimal,\n    ];\n}