<?php\n    // Set user's locale (this is just an example, in real app this should be dynamic)\n    $userLocale = 'fr_FR';\n\n    putenv("LC_ALL=$userLocale");\n    setlocale(LC_ALL, $userLocale);\n\n    // Specify the location of translation dictionaries\n    bindtextdomain("myapp", "./LocaleData");\n\n    // Select which domain to use by default when calling _\n    textdomain("myapp");\n\n    echo _('Hello, World!');