$connection = mysqli_connect("localhost", "username", "password", "database_name") or die(mysqli_error($connection));\n\n$sections_query = mysqli_query($connection, 'SELECT * FROM sections') or die(mysqli_error($connection));\n\nwhile ($row_sections = mysqli_fetch_assoc($sections_query)) {\n    echo "<h3>" . $row_sections['section_name'] . "</h3>";\n    echo "<ul>";\n\n    // Assuming you have a way to dynamically determine the section_id for each section\n    $section_id = $row_sections['section_id'];\n    $awards_sql = mysqli_query($connection, "SELECT * FROM categories WHERE section_id = $section_id") or die(mysqli_error($connection));\n\n    while ($categories = mysqli_fetch_assoc($awards_sql)) {\n        echo "<li><strong>" . htmlspecialchars($categories['category_name']) . "</strong></li>";\n    }\n    echo "</ul>";\n}\n\nmysqli_close($connection);