<?php\n\nfunction parseString($input) {\n    // Define the pattern to match the required groups\n    $pattern = '/^(.*?)\s*(?:\((.*?)\))?\s*(?:\[([^\]]*)\])?$/';\n    \n    // Use preg_match to extract the parts\n    if (preg_match($pattern, $input, $matches)) {\n        // Remove the full match from the beginning of the array\n        array_shift($matches);\n        \n        return $matches;\n    }\n    \n    // Return false or an empty array if no matches are found\n    return [];\n}\n\n// Example usage:\n$strings = [\n    "Some place",\n    "Some place (often text in parenthesis)",\n    "Some place (often text in parenthesis) [even text in brackets sometimes]"\n];\n\nforeach ($strings as $string) {\n    print_r(parseString($string));\n}\n?>