function getLogs($connection) {\n    $logs = [];\n    $result = $connection->query("SELECT * FROM log");\n    \n    while ($logRow = $result->fetch_assoc()) {\n        switch ($logRow['reference_table_name']) {\n            case 'users':\n                $userResult = $connection->query("SELECT name FROM users WHERE id = {$logRow['reference_id']}");\n                if ($userNameRow = $userResult->fetch_assoc()) {\n                    $logRow['reference_label'] = $userNameRow['name'];\n                }\n                break;\n            case 'services':\n                $serviceResult = $connection->query("SELECT service_name FROM services WHERE id = {$logRow['reference_id']}");\n                if ($serviceNameRow = $serviceResult->fetch_assoc()) {\n                    $logRow['reference_label'] = $serviceNameRow['service_name'];\n                }\n                break;\n            // Add more cases as needed\n        }\n        $logs[] = $logRow;\n    }\n    \n    return $logs;\n}