<?php\n   // Assuming $pdo is already defined and connected as above\n\n   try {\n       // Prepare SQL statement using FULLTEXT search\n       $stmt = $pdo->prepare("SELECT * FROM books WHERE MATCH (title) AGAINST (:keyword IN NATURAL LANGUAGE MODE)");\n       $stmt->bindValue(':keyword', $keyword);\n       $stmt->execute();\n\n       // Fetch results\n       $books = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n       foreach ($books as $book) {\n           echo htmlspecialchars($book['title']) . '<br>';\n       }\n   } catch (PDOException $e) {\n       echo 'Connection failed: ' . $e->getMessage();\n   }\n   ?>